import React from "react";
import { useState,useEffect } from "react";
import axios from "axios";


import { Card } from "react-bootstrap";

import "./index.css";

const Temp = () => {

  const [cityName,setcityName] = useState(null);
  const [search,setSearch] =useState("Jaipur");

  
const searchLocation = ()=>{
  
  const api =`https://api.openweathermap.org/data/2.5/weather?q=${search}&appid=7adbc70eb67527ffb81d0b74c1bfa0e7`;
axios.get(api).then()

} 

  
   

  
  
  return (
    <>
      <div className="main_card">
        <Card className="card">
        
            <div className="Search_Bar">
              <div className="input-group">
                <div className="form-outline">
                  <input type="text" className="form-control" onChange={(e)=>{
                    console.log(e.target.value);
setSearch(e.target.value);
                  }} />
                </div>

                <button type="button" className="btn search_button btn-primary" onClick={searchLocation}>
                  <i className="fas fa-search"></i>
                </button  >
              </div>
            </div>
            <Card.Body className="temp_contianer">
            
              <h1 className="cityName">
               
                <i className="fa-solid fa-location-dot"> </i> {cityName}
              </h1>
              <h1 className="temp">{cityName}</h1>
              <p >Cloudy</p>
              <h3 className="tempMinMax"> Min:20°C | Max :20°C</h3>
          
          </Card.Body>
          <Card.Footer className="bottom">
        
              <div className="speed">
              <p className="fw-bold"> 2mbps</p>
              <p>Wind Speed</p>
              </div>
              <div className="pressure">
                <p className="fw-bold">1006</p>
                <p>pressure</p>
              </div>
          <div className="humidity">
            <p className="fw-bold">70%</p>
            <p>Humidity</p>
          
          </div>
          </Card.Footer>
             
            
          
         
        </Card>
      </div>
    </>
  );
};

export default Temp;
