import React from "react";
import {useEffect, useState} from "react";

import axios from "axios";


import { Card } from "react-bootstrap";

import "./index.css";



const Tempapp = () => {

  const [data, setData] = useState(null)
  const [city,setCity] =useState("jaipur");

  
 
useEffect(()=>{
  const fetchApi= async()=>{

// const url =`https://api.openweathermap.org/data/2.5/weather?q=${city}&units=metric&appid=7adbc70eb67527ffb81d0b74c1bfa0e7`;
// const response = await fetch(url);
// const resjson = await response.json();

// console.log(resjson)
// setData(resjson.main);
let api =`https://api.openweathermap.org/data/2.5/weather?q=${city}&units=metric&appid=7adbc70eb67527ffb81d0b74c1bfa0e7`;
      axios.get(api).then((response) => {
        setData(response.data)
     
      })


}
fetchApi();
},[city])



console.log(data);

//  const searchLocation = (event) => {
   
//   let api =`https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=7adbc70eb67527ffb81d0b74c1bfa0e7`;
//       axios.get(api).then((response) => {
//         setData(response.data)
     
//       })
 
  
//   }
//   searchLocation();



  
  return (
    <>
      <div className="main_card">
        <Card className="card">
        
            <div className="Search_Bar">
              <div className="input-group">
                <div className="form-outline">
                  <input type="text" 
                    className="form-control"
                  
                 onChange={(e)=>{
setCity(e.target.value);
                  }} />
                </div>
              

                <button type="button"   className="btn search_button btn-primary">
                  <i className="fas fa-search"></i>
                </button  >
              </div>
            </div>
{!data ? (<h1>data not found</h1>):(
 
 
  <>
                   <Card.Body className="temp_contianer">
            
                   <h1 className="cityName">
                    
                     <i className="fa-solid fa-location-dot"> </i>{city}
                   </h1>
                   <h1 className="temp">{data.main.temp.toFixed()}°C</h1>
                   <p>{data.weather[0].icon}</p>
                   <p>{data.weather[0].main}</p>
                  <h3 className="tempMinMax">max : {data.main.temp_max}°C | min : {data.main.temp_min}°C</h3>
               
                  </Card.Body>
               <Card.Footer className="bottom">
             
                   <div className="speed">
                   <p className="fw-bold">{data.wind.speed.toFixed() *3.6} km/h</p>
                   <p>Wind Speed</p>
                   </div>
                   <div className="pressure">
                     <p className="fw-bold">{data.main.pressure} hPa</p>
                     <p>pressure</p>
                   </div>
               <div className="humidity">
                 <p className="fw-bold">{data.main.humidity}%</p>
                 <p>Humidity</p>
               
               </div>
           
               </Card.Footer>
              
                
               </>
              )
}

             
            
          
         
        </Card>
      </div>
    </>
  );
};

export default Tempapp;
