import React from "react";
import { useState,useEffect} from "react";
import axios from "axios";


import { Card } from "react-bootstrap";

import "./index.css";


const Temp = () => {

  const [data, setData] = useState(null)
  const [city,setCity] =useState("jaipur");

  
 






//   let api =`https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=7adbc70eb67527ffb81d0b74c1bfa0e7`;
//  const searchLocation = (event) => {
//     if (event.key === 'Enter') {
//       axios.get(api).then((response) => {
//         setData(response.data)
//        console.log(response.data.name)
//         console.log(response.data)
//       })
 
//     }
//   }
//   searchLocation();



  
  return (
    <>
      <div className="main_card">
        <Card className="card">
        
            <div className="Search_Bar">
              <div className="input-group">
                <div className="form-outline">
                  <input type="text" 
                    className="form-control"
                  
                 onChange={(e)=>{
                   console.log(e.target.value)
setCity(e.target.value);
                  }} />
                </div>
              

                <button type="button"  className="btn search_button btn-primary">
                  <i className="fas fa-search"></i>
                </button  >
              </div>
            </div>

            { !data ? (<p>no data</p>):(<>
                   <Card.Body className="temp_contianer">
            
                   <h1 className="cityName">
                    
                     <i className="fa-solid fa-location-dot"> </i>{city}
                   </h1>
                   <h1 className="temp">20°C</h1>
                   <p >cloudy</p>
                  <h3 className="tempMinMax">Max:{data.main.temp_max.toFixed()}°C | Max : {data.main.temp_min.toFixed()}°C</h3>
               
               </Card.Body>
               <Card.Footer className="bottom">
             
                   <div className="speed">
                   <p className="fw-bold">{data.wind.speed}MPH</p>
                   <p>Wind Speed</p>
                   </div>
                   <div className="pressure">
                     <p className="fw-bold">{data.main.pressure}</p>
                     <p>pressure</p>
                   </div>
               <div className="humidity">
                 <p className="fw-bold">{data.main.humidity}%</p>
                 <p>Humidity</p>
               
               </div>
               </Card.Footer>
               </>
                  )}
           
             
            
          
         
        </Card>
      </div>
    </>
  );
};

export default Temp;
