import React from "react";
import { useState} from "react";
import axios from "axios";


import { Card } from "react-bootstrap";

import "./index.css";
import { useEffect } from "react";

const Temp = () => {
  const [data, setData] = useState({})
  const [location, setLocation] = useState('')

  
 



// useEffect =(()=>{


  let url =`https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=7adbc70eb67527ffb81d0b74c1bfa0e7`;
  const searchLocation = (event) => {
    if (event.key === 'Enter') {
      axios.get(url).then((response) => {
        setData(response.data)
        console.log(response.data)
      })
      setCity('')
    }
  }
//   const fetchApi = async()=>{
   
//     const url =`https://api.openweathermap.org/data/2.5/weather?q=${city}& units=metric&appid=7adbc70eb67527ffb81d0b74c1bfa0e7`;
//     const res = await fetch(url);
//     const resjson = await res.json();
//     console.log(resjson);
//     setData(resjson);
//   }
//   fetchApi();

// },[city]);


  
  return (
    <>
      <div className="main_card">
        <Card className="card">
        
            <div className="Search_Bar">
              <div className="input-group">
                <div className="form-outline">
                  <input type="text" 
                    className="form-control"
                    onKeyPress={ searchLocation}
                 onChange={(e)=>{
                   console.log(e.target.value)
setCity(e.target.value);
                  }} />
                </div>
              

                <button type="button"  className="btn search_button btn-primary">
                  <i className="fas fa-search"></i>
                </button  >
              </div>
            </div>

            { !data?(<p>no data</p>):(<>
                   <Card.Body className="temp_contianer">
            
                   <h1 className="cityName">
                    
                     <i className="fa-solid fa-location-dot"> </i>{city}
                   </h1>
                   <h1 className="temp">{data.main.temp.toFixed()}°C</h1>
                   <p >{data.weather[0].main}</p>
                  <h3 className="tempMinMax">Max:{data.main.temp_max.toFixed()}°C | Max : {data.main.temp_min.toFixed()}°C</h3>
               
               </Card.Body>
               <Card.Footer className="bottom">
             
                   <div className="speed">
                   <p className="fw-bold">{data.wind.speed}MPH</p>
                   <p>Wind Speed</p>
                   </div>
                   <div className="pressure">
                     <p className="fw-bold">{data.main.pressure}</p>
                     <p>pressure</p>
                   </div>
               <div className="humidity">
                 <p className="fw-bold">{data.main.humidity}%</p>
                 <p>Humidity</p>
               
               </div>
               </Card.Footer>
               </>
                  )}
           
             
            
          
         
        </Card>
      </div>
    </>
  );
};

export default Temp;
