import React from "react";
import {useEffect, useState} from "react";

import axios from "axios";


import { Card } from "react-bootstrap";

import "./index.css";



const Tempapp = () => {

  const [data, setData] = useState(null)
  const [city,setCity] =useState("jaipur");

  
 
useEffect(()=>{
  const fetchApi= async()=>{

const url =`https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=7adbc70eb67527ffb81d0b74c1bfa0e7`;
const response = await fetch(url);
const resjson = await response.json();

console.log(resjson)
setData(resjson);


}
fetchApi();
},[city])





//  const searchLocation = (event) => {
   
//   let api =`https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=7adbc70eb67527ffb81d0b74c1bfa0e7`;
//       axios.get(api).then((response) => {
//         setData(response.data)
     
//       })
 
  
//   }
//   searchLocation();



  
  return (
    <>
      <div className="main_card">
        <Card className="card">
        
            <div className="Search_Bar">
              <div className="input-group">
                <div className="form-outline">
                  <input type="text" 
                    className="form-control"
                  
                 onChange={(e)=>{
setCity(e.target.value);
                  }} />
                </div>
              

                <button type="button"   className="btn search_button btn-primary">
                  <i className="fas fa-search"></i>
                </button  >
              </div>
            </div>
{data.ci &&
 
  <div className="details">
                   <Card.Body className="temp_contianer">
            
                   <h1 className="cityName">
                    
                     <i className="fa-solid fa-location-dot"> </i>{city}
                   </h1>
                   <h1 className="temp">{data.main.temp}°C</h1>
                   <p >cloudy</p>
                  <h3 className="tempMinMax">max 20°C | min 20</h3>
               
                  </Card.Body>
               <Card.Footer className="bottom">
             
                   <div className="speed">
                   <p className="fw-bold">20MPH</p>
                   <p>Wind Speed</p>
                   </div>
                   <div className="pressure">
                     <p className="fw-bold">20</p>
                     <p>pressure</p>
                   </div>
               <div className="humidity">
                 <p className="fw-bold">20%</p>
                 <p>Humidity</p>
               
               </div>
           
               </Card.Footer>
              
                
               </div>
 
}

             
            
          
         
        </Card>
      </div>
    </>
  );
};

export default Tempapp;
